sqlMap.select:
SELECT b.* FROM ( SELECT row_number() over(order by regDate desc) recNum ,count(*) over() totalCnt2 ,a.* FROM ( SELECT '1' gubn, BOARD_UNIV_ID id ,TITLE title ,DBMS_LOB.INSTR(CONTENT,100,0) content ,REG_DATE regDate FROM CU_BOARD_UNIV WHERE ( ( TITLE LIKE '%'||#headerText1#||'%'
TITLE LIKE '%'||#headerText2#||'%'
TITLE LIKE '%'||#headerText3#||'%'
) OR ( CONTENT LIKE '%'||#headerText1#||'%'
CONTENT LIKE '%'||#headerText2#||'%'
CONTENT LIKE '%'||#headerText3#||'%'
) ) AND TO_CHAR(SYSDATE,'YYYYMMDD') BETWEEN EVENT_SDT AND EVENT_EDT UNION ALL SELECT '2' gubn, BOARD_ID id ,TITLE title ,DBMS_LOB.INSTR(CONTENT,100,0) content ,REG_DATE regDate FROM CU_BOARD WHERE ( ( TITLE LIKE '%'||#headerText1#||'%'
TITLE LIKE '%'||#headerText2#||'%'
TITLE LIKE '%'||#headerText3#||'%'
) OR ( CONTENT LIKE '%'||#headerText1#||'%'
CONTENT LIKE '%'||#headerText2#||'%'
CONTENT LIKE '%'||#headerText3#||'%'
) ) UNION ALL SELECT '3' gubn ,FAQ_ID id ,QUESTION title ,DBMS_LOB.INSTR(ANSWER,100,0) content ,REG_DATE regDate FROM CU_FAQ WHERE ( ( QUESTION LIKE '%'||#headerText1#||'%'
QUESTION LIKE '%'||#headerText2#||'%'
QUESTION LIKE '%'||#headerText3#||'%'
) OR ( ANSWER LIKE '%'||#headerText1#||'%'
ANSWER LIKE '%'||#headerText2#||'%'
ANSWER LIKE '%'||#headerText3#||'%'
) ) AND USE_YN = 'Y' ) a ) b WHERE CEIL (recNum / #showCnt2,javaType=Integer#) = #currPage2,javaType=Integer# ORDER BY recNum ASC
